CREATE TABLE "public"."contest_results" ("id" text NOT NULL DEFAULT gen_random_uuid(), "accountId" text NOT NULL, "questionId" text NOT NULL, "point" float8, "completionTime" Time, "status" text NOT NULL DEFAULT '"active"', "createdAt" timestamptz NOT NULL DEFAULT now(), "createdBy" text, "updatedAt" timestamptz NOT NULL DEFAULT now(), "updatedBy" text, "contestId" text NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("accountId") REFERENCES "public"."account"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("contestId") REFERENCES "public"."contests"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("questionId") REFERENCES "public"."questions"("id") ON UPDATE restrict ON DELETE restrict);
